            Thread.sleep(1000);
            BufferedReader in = new BufferedReader(new InputStreamReader(socket
                    .getInputStream()));
            current=socket;
            while (true) {
                //
                String jieshou = in.readLine();
                System.out.println(jieshou);
                textArea1.append(jieshou+"\n");
                String[] neirong=jieshou.split(":");
                String buf1=neirong[0],buf2;;
                if(neirong.length==1) { 	
                	buf2=" ";}
                else {
                	buf2=neirong[1];
                }
                sqltest.insert(buf1, buf2);
                //
                for (int i = 0; i < socketList.size(); i++) {
                    Socket socket=socketList.get(i);
                    PrintWriter out = new PrintWriter(socket.getOutputStream());
                    if (socket!=current) {
                        out.println(jieshou);
                    }else{
                        out.println("()"+jieshou);
                    }
                    out.flush();
                }
            }
        } catch (Exception e) {
        	socketList.remove(current);
        	Print.socketList.remove(current);
    		textArea1.append(""+"\n");
            e.printStackTrace();
        } 
    }
}
